mapper.select:
SELECT 	* 
FROM 	consultaodontologicoadministrativa consultaOdontologico


		    consultaOdontologico.codigoAlumno = #{codigoAlumno}


		AND consultaOdontologico.tipoAlumno = #{tipoAlumno}


		AND consultaOdontologico.idCampania = #{idCampania}


		AND consultaOdontologico.codigoFacultad = #{codigoFacultad}


		AND consultaOdontologico.codigoEscuela = #{codigoEscuela}


		AND consultaOdontologico.soloFechaRegistro BETWEEN #{fechaInicio} AND #{fechaFin}


ORDER BY fechaRegistro DESC
